import { ProductsService } from './../../../services/products.service';
import { Component, OnInit } from '@angular/core';
import ProductModel from 'src/app/models/product.model';
import { NotifyService } from 'src/app/services/notify.service';
import { CategoryService } from 'src/app/services/category.service';
import CategoryModel from 'src/app/models/category.model';
import UserModel from 'src/app/models/user.model';
import { Unsubscribe } from 'redux';
import store from 'src/app/redux/store';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
    selector: 'app-product-list',
    templateUrl: './product-list.component.html',
    styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit {

    public products: ProductModel[];
    public categories: CategoryModel[];
    public category: CategoryModel;
    public searchedKeyword: string;
    public user: UserModel;
    private unsubscribeMe: Unsubscribe;
    public id: string;

    constructor(
        private myProductsService: ProductsService,
        private myCategoriesService: CategoryService,
        private myActivatedRoute: ActivatedRoute,
        private myRouter: Router
        ) { }

    async ngOnInit() {
        // this.unsubscribeMe = store.subscribe(() => {
        this.user = store.getState().authState.user;
        // });
        try {
            this.products = await this.myProductsService.getAllProducts();
        }
        catch (err) {
            console.log(err);
        }
        try {
            this.id = this.myActivatedRoute.snapshot.params.id; // Take a route parameter named id
            this.category = await this.myCategoriesService.getOneCategory(this.id);
            console.log(this.category);
        }
        catch (err) {
            console.log(err);
        }
        try {
            this.categories = await this.myCategoriesService.getAllCategories();
        }
        catch (err) {
            console.log(err);
        }
    }

    async routerLink(id: string) {
        try {
            this.id = this.myActivatedRoute.snapshot.params.id; // Take a route parameter named id
            this.category = await this.myCategoriesService.getOneCategory(id);
            console.log(this.category);
        }
        catch (err) {
            console.log(err);
        }
        this.myRouter.navigateByUrl("/products/category/" + this.id);
    }

    // ngOnDestroy(): void {
    //     this.unsubscribeMe();
    // }

}
<mat-drawer-container class="main" autosize>
    <mat-drawer #drawer class="cart" mode="side" #right position="start">
        <app-my-cart></app-my-cart>
    </mat-drawer>
    <div class="products">
        <div class="search">
            <label>Search: </label>
            <input type="text" [(ngModel)]="searchedKeyword"/>
            <a [routerLink]="'/products/search/' + searchedKeyword">
                <button mat-stroked-button>
                    <mat-icon>search</mat-icon>
                </button>
            </a>
        </div>
        <br/>
        <div class="links">
            <button type="button" mat-stroked-button (click)="drawer.toggle()">My Cart</button>
            <a mat-stroked-button routerLink="/products">All Products</a>
            <a *ngFor="let category of categories" mat-stroked-button (click)="routerLink(category._id)">{{category.name}}</a>
        </div>
        <br/>
        <hr/>
        <app-please-wait *ngIf="!products"></app-please-wait>
        <app-product-card *ngFor="let p of products" [product]="p" [category]></app-product-card>
    </div>
</mat-drawer-container>
