import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import CategoryModel from 'src/app/models/category.model';
import { CategoryService } from 'src/app/services/category.service';

@Component({
  selector: 'app-categories',
  templateUrl: './categories.component.html',
  styleUrls: ['./categories.component.css']
})
export class CategoriesComponent implements OnInit {

    public category: CategoryModel;
    public categories: CategoryModel[];
    public searchedKeyword: string;
    public id: string;

    constructor(
        private myActivatedRoute: ActivatedRoute,
        private myRouter: Router,
        private myCategoriesService: CategoryService) { }

    async ngOnInit() {
        try {
            const id = this.myActivatedRoute.snapshot.params.id;
            this.category = await this.myCategoriesService.getOneCategory(id);
            console.log("ngoninit");
            console.log(this.category.name);
            }
        catch (err) {
            console.log(err);
        }
        try {
            this.categories = await this.myCategoriesService.getAllCategories();
        }
        catch (err) {
            console.log(err);
        }
    }

    async routerLink() {
        try {
            this.id = this.myActivatedRoute.snapshot.params.id;
            this.category = await this.myCategoriesService.getOneCategory(this.id);
            console.log("router");
            console.log(this.category.name);
        }
        catch (err) {
            console.log(err);
        }
        this.myRouter.navigateByUrl("/products/category/" + this.id);
    }
}
<mat-drawer-container class="example-container" autosize>
    <mat-drawer #drawer class="example-sidenav2" mode="side" #right position="start">
        <app-my-cart></app-my-cart>
    </mat-drawer>
    <div class="example-sidenav-content">
        <div class="products">
            <div class="search">
                <label>Search: </label>
                <input type="text" [(ngModel)]="searchedKeyword"/>
                <a [routerLink]="'/products/search/' + searchedKeyword">
                    <button mat-stroked-button>
                        <mat-icon>search</mat-icon>
                    </button>
                </a>
            </div>
            <br/>
            <div class="links">
                <button type="button" mat-stroked-button (click)="drawer.toggle()">My Cart</button>
                <a mat-stroked-button routerLink="/products">All Products</a>
                <a *ngFor="let category of categories" mat-stroked-button routerLink="/products/category/{{category._id}}" (click)="routerLink()">{{category.name}}</a>
            </div>
            <br/>
            <hr/>
            <app-please-wait *ngIf="!category"></app-please-wait>
            <app-product-card *ngFor="let p of category.products" [product]="p" [category]></app-product-card>
        </div>
    </div>
</mat-drawer-container>
