import { Component, OnInit, Input } from '@angular/core';
import { Unsubscribe } from 'redux';
import CartModel from 'src/app/models/cart.model';
import ItemModel from 'src/app/models/item.model';
import OrderModel from 'src/app/models/order.model';
import UserModel from 'src/app/models/user.model';
import { NotifyService } from 'src/app/services/notify.service';
import { OrdersService } from 'src/app/services/orders.service';
import store from 'src/app/redux/store';
import { CartsService } from 'src/app/services/carts.service';
import { ItemsService } from 'src/app/services/items.service';
import { MatDialog } from '@angular/material/dialog';
import { DialogComponent } from '../dialog/dialog.component';
import { Router } from '@angular/router';
import { of } from 'rxjs';  
import { distinct } from 'rxjs/operators';
import { BookeddatesService } from 'src/app/services/bookeddates.service';

export interface DialogData {
  quantity: number;
}

@Component({
  selector: 'app-my-order',
  templateUrl: './my-order.component.html',
  styleUrls: ['./my-order.component.css'],
})

export class MyOrderComponent implements OnInit {

  private carts: CartModel[];
  private items: ItemModel[];
  public orders: OrderModel[];
  public bookedDate: OrderModel[];
  public user: UserModel;
  public order = new OrderModel();
  public cart: CartModel;
  public totalPrice: number = 0;
  public unsubscribeMe: Unsubscribe;
  private lastCartIndex: number;
  public dialogRef: any;
  public result: any;
  public today = new Date();

  constructor(
    private myCartsService: CartsService,
    private myOrdersService: OrdersService,
    private myBookedDatesService: BookeddatesService,
    private myItemsService: ItemsService,
    private notify: NotifyService,
    public dialog: MatDialog,
    private myRouter: Router
  ) { }

  async ngOnInit() {
    // this.unsubscribeMe = store.subscribe(async () => {
      this.user = store.getState().authState.user;
      try {
        this.carts = await this.myCartsService.getAllCarts(this.user._id);
      }
      catch (err) {
        console.log("err getting user carts");
      }
      // try {
      // this.orders = await this.myOrdersService.getAllOrders();
      // }
      // catch (err) {
      //     this.notify.error("err getting user carts");
      // }
      // const takenShippinDate = this.orders.count([
      //   {"$group" : { "order": "$shippingDate", "count": { "$sum": 3 } } },
      //   ]);
      // console.log(takenShippinDate);
    // });
  }

  // ngOnDestroy(): void {
  //   this.unsubscribeMe();
  // }

  getUsersCity() {
    // this.user = store.getState().authState.user;
    this.order.city=this.user.city;
  }

  getUsersSt() {
    // this.user = store.getState().authState.user;
    this.order.st = this.user.st;
  }

  myFilter = (d: Date | null): boolean => {
    const day = (d || new Date()).getDay();
    return day !== 5 && day !== 6;
  };

  async dateValidation() {
    console.log("here");
    // const myFilter = (d: Date | null): boolean => {
      this.user = store.getState().authState.user;
      try {
        this.orders = await this.myOrdersService.getAllUserOrders(this.user._id);
      }
      catch (err) {
        console.log("err getting user carts");
      }
      try {
        this.bookedDate = await this.myBookedDatesService.getAllBookedDates();
      }
      catch (err) {
        console.log("err getting booked dates");
      }
      // const uniqueShippinDateArray = this.orders.distinct('shippingDate');
      // console.log(uniqueShippinDateArray);
      // const day = (d || new Date()).getDay();
      // Prevent Saturday and Sunday from being selected.
      // return day !== 5 && day !== 6 && day !== takenShippinDate;
    // public disabledDates = (date: Date): boolean => {
    // return date.getDate() % 2 === 0;
    // };
    // };

    // try {
    //   this.orders = await this.myOrdersService.getAllOrders();
    // }
    // catch (err) {
    //     this.notify.error(err);
    // }
    // const takenShippinDate = this.orders.count(o.shippingDate === 3);
    // 
    // let picker = (d: Date | null): boolean => {
    //   const day = (d || new Date()).getDay();
    //   // Prevent Saturday and Sunday from being selected.
    //   return day !== 0 && day !== 6;
    // };
  }


  // doesn work
  cardValidation() {
    const cardRegEx = '/^(?:4[0-9]{12}(?:[0-9]{3})?)$/ || /^(?:5[1-5][0-9]{14})$/ || /^(?:3[47][0-9]{13})$/ || /^(?:6(?:011|5[0-9][0-9])[0-9]{12})$/';
    if (this.order.card !== cardRegEx) {
      return false;
    } else {
      return true;
    }
  }

  async makeOrder() {
    this.user = store.getState().authState.user;
    try {
      this.carts = await this.myCartsService.getAllCarts(this.user._id);
      console.log(this.carts);
    }
    catch (err) {
        this.notify.error("err getting user carts-order page");
    }
    this.order.user = this.user;
    this.lastCartIndex = this.carts.length - 1;
    try {
      this.items = await this.myItemsService.getAllItems(this.carts[this.lastCartIndex]._id);
    }
    catch (err) {
        this.notify.error("err getting user carts");
    }
    this.order.cart = this.carts[this.lastCartIndex];
    // for (let item of this.items) {
    //   this.totalPrice += item.price;
    // }
    for (let item of this.items) {
      this.totalPrice += item.price;
    }
    this.order.price = this.totalPrice;
    this.order.orderDate = new Date().toLocaleDateString();
    this.order.shippingDate = this.order.shippingDate.toLocaleString();

    try {
      this.order.card = this.order.card.toString();
      this.order.card = this.order.card.substring(this.order.card.length - 4);
      this.order = await this.myOrdersService.addOrder(this.order);
    }
    catch (err) {
      console.log("problem creating order");
    }

    console.log(this.order);
    this.dialogRef = this.dialog.open(DialogComponent, {
      data: {invoice: this.cart},
    });

    this.dialogRef.afterClosed().subscribe((result: any) => {
      console.log('The dialog was closed');
      this.myRouter.navigateByUrl("/home");
      this.result = result;
    });

  }

}
