import { UserService } from './../../../services/user.service';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { UserModel } from 'src/app/models/user.model';
import { AuthService } from 'src/app/services/auth.service';
import { NotifyService } from 'src/app/services/notify.service';


@Component({
    selector: 'app-register',
    templateUrl: './register.component.html',
    styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {

    public user = new UserModel();
    private usersList: UserModel[];
    public confirm: boolean;

    constructor(
        private myAuthService: AuthService,
        private notify: NotifyService,
        private myUserService: UserService,
        // private userModel: UserModel,
        private myRouter: Router) { }

    public async register() {
        try {
            await this.myAuthService.register(this.user);
            this.notify.success("You are registered :-)");
            this.myRouter.navigateByUrl("/home");
        }
        catch(err) {
            this.notify.error(err);
        }
    }

    public confirmFun() {
        if (this.user.password === this.user.confirmPassword) {
            this.confirm = true;
        } else {
            this.confirm = false;
        }
        console.log("confirm: " + this.confirm)
        return this.confirm;
    } 

    public async ngOnInit() {
        try {
            // 2. goes to database and searches if email exsits there
            // 2a. add user service in order to GET / check emails
                this.usersList = await this.myUserService.getAllUsers();
            }   catch(err) {
                    this.notify.error(err);
            }   
            // console.log(this.usersList);
    }
    public checkEmail() {
        // 1. gets the email that was inserted in html (this.email)
        const email = this.user.email

        const result = this.usersList.filter(userObj => userObj.email === email);
        // 3. if email exists: alert user via span
        if (result.length >= 1) {
            return true;
        } else {
            return false;
        }
    }


    public checkId() {
        // 1. gets the email that was inserted in html (this.email)
        const id = this.user.id

        const result = this.usersList.filter(userObj => userObj.id === id);

        // 3. if email exists: alert user via span
        if (result.length >= 1) {
            return true;
        } else {
            return false;
        }
    }

